import React from 'react'
import { useAppDispatch, useAppSelector } from '../../../app/hooks'
import { selectedGuidFilter } from '../../guides/filterSlice'

export const GuideByFilter = () => {
  const dispatch = useAppDispatch()
  const filterSelector = useAppSelector(selectedGuidFilter)
  function handleSearch(ev:any){
    try {
      let {country, city } = ev.target.elements;
      country = country.value;
      city = city.value;
      dispatch({country, city})
    } catch (error) {
      console.log(error);
      
    }
  }
  return (
    <div>
      <form onSubmit={handleSearch}>
        <input type="text" name='country' placeholder='country'/>
        <input type="text" name='city' placeholder='city' />
        <input type="submit" value="search" />
      </form>
    </div>
  )
}
