import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { RootState } from "../../app/store";
import { guidesByFilterAsync } from "./guideAPI";
import { GuideDetails } from "./guideSlice";

export interface FilterGuide{
    guid: GuideDetails | null,
    status: "idle" | "loading" | "failed",
};

const initialState = {
    guid: null,
    status: 'idle'
};

const filterGuidSlice = createSlice({
    name: 'GuidByFilter',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder
            .addCase(guidesByFilterAsync.pending, (state) => {
                state.status = 'loading'
            })
            .addCase(guidesByFilterAsync.fulfilled)
    }

});

export default filterGuidSlice.reducer;
