import React, { useEffect, useRef } from 'react'
import { useAppDispatch, useAppSelector } from '../../../app/hooks'
import { selectedFilterStatus, selectedGuidFilter } from '../../guides/filterSlice'
import { guidesByFilterAsync } from '../../guides/guideAPI';
import { GuideDetails } from '../../guides/guideSlice';
import { GuidesCard } from './GuidesCard';

export const GuideByFilter = () => {
  const dispatch = useAppDispatch()
  const country = React.useRef<any>()
  const city = React.useRef<any>()
  function handleSearch(){
    try {
      // let {country, city } = ev.target.elements;
      // country = country.value;
      // city = city.value;
      dispatch(guidesByFilterAsync({country, city }))
      
      // console.log(country, city);
      
    } catch (error) {
      console.log(error);
      
    }
  };
  useEffect(() => {
    handleSearch()  
  },[handleSearch])
  const filterSelector = useAppSelector(selectedGuidFilter);
  const statusSelector = useAppSelector(selectedFilterStatus);
  
  console.log(filterSelector);
  
  return (
    <div>
      <form onSubmit={handleSearch}>
        <input type="text" name='country' ref={country} placeholder='country'/>
        <input type="text" name='city' ref={city} placeholder='city' />
        <input type="submit" value="search" />
      </form>
      { filterSelector.map((filGuide: any) => {
        return (
          <GuidesCard fullName={filGuide.fullName}
                        country={filGuide.country}
                        city={filGuide.city}
                        telephon={filGuide.telephon}
                        email={filGuide.email}
                        image={filGuide.image}/>
          )
        })}
        
    
    </div>
  )
}
