import {createAsyncThunk} from '@reduxjs/toolkit';
import axios from 'axios';
import { config } from 'dotenv';

interface guideReg{
    fullName: string,
    country: string,
    city: string,
    telephon: string,
    email: string,
    formData: File
}

export const addGuid = createAsyncThunk(
    'addGuid',
    async({fullName, country, city, telephon, email, formData}: guideReg) => {
        const image = new FormData();
        image.append('image', formData)
        // const config = {
        //     headers: {
        //      'content-type': 'multipart/form-data' 
        //     }
        //   };

        const result = await axios.post
            ('/api-guides/add-guide',
            {fullName, country, city, telephon, email, image});
            await axios.post('/api-guides/add-guide'. image, config)
         console.log(result.data);
         return result.data;
         

    }
)
