import {createAsyncThunk} from '@reduxjs/toolkit';

import axios from 'axios';
import { config } from 'dotenv';

interface guideReg{
    fullName: string,
    country: string,
    city: string,
    telephon: string,
    email: string,
    image: string
}

export const addGuid = createAsyncThunk(
    'addGuid',
    async({fullName, country, city, telephon, email, image}: guideReg) => {
        let abc = await axios.post("/d/",{a:1,b:2})
        console.log(abc)
        const imgSrc = Buffer.from(image, 'base64')
        let result = await axios.post
            ('/api-guides/add-guide',
            {fullName, country, city, telephon, email, image: imgSrc}).then(res=> "aaa").catch(err=>console.log(err));
         console.log(result);
         
         return result;
         

    }
)
