import axios from 'axios';
import { config } from 'dotenv';

interface guideReg{
    fullName: string,
    country: string,
    city: string,
    telephon: string,
    email: string,
    image: File
}

export const addGuid = createAsyncThunk(
    'addGuid',
    async({fullName, country, city, telephon, email, image}: guideReg) => {
        const file = new FormData();
        file.append('image', formData)
        // const config = {
