import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { RootState } from "../../app/store";
import { guidesByFilterAsync } from "./guideAPI";
import { GuideDetails } from "./guideSlice";

export interface FilterGuide{
    guid: GuideDetails[] ,
    status: "idle" | "loading" | "failed",
};

const initialState: FilterGuide = {
    guid: [],
    status: 'idle'
};

const filterGuidSlice = createSlice({
    name: 'GuidByFilter',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder
            .addCase(guidesByFilterAsync.pending, (state) => {
                state.status = 'loading'
            })
            .addCase(guidesByFilterAsync.fulfilled, (state, action: PayloadAction<any>) => {
                state.status = 'idle'
                const {guid} = action.payload
                state.guid = guid 
            })
            .addCase(guidesByFilterAsync.rejected, (state) => {
                state.status = 'failed'
            })
    }

});


export const selectedGuidFilter = (state: RootState) => state.GuideByFilter.guid;
export const selectedFilterStatus = (state: RootState) => state.GuideByFilter.status;
export default filterGuidSlice.reducer;
