import axios from 'axios';
import { config } from 'dotenv';

interface guideReg{
    fullName: string,
    country: string,
    city: string,
    telephon: string,
    email: string,
    image: File
}

export const addGuid = createAsyncThunk(
    'addGuid',
    async({fullName, country, city, telephon, email, image}: guideReg) => {
        
        const base = JSON.stringify(image)
        const base64 = btoa(image)
        const result = await axios.post
            ('/api-guides/add-guide',
            {fullName, country, city, telephon, email});
         console.log(result.data);
         
         return result.data;
         

    }
)
